// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.18;

contract EvidenceStorage {
    struct Evidence {
        string ipfsHash; // or file hash
        uint timestamp;
        address submittedBy;
    }

    mapping(uint => Evidence) public evidences;
    uint public evidenceCount;

    event EvidenceSubmitted(uint id, address indexed submittedBy, string ipfsHash, uint timestamp);

    function submitEvidence(string calldata ipfsHash) external {
        evidenceCount++;
        evidences[evidenceCount] = Evidence(ipfsHash, block.timestamp, msg.sender);
        emit EvidenceSubmitted(evidenceCount, msg.sender, ipfsHash, block.timestamp);
    }

    function getEvidence(uint id) external view returns (string memory, uint, address) {
        Evidence memory e = evidences[id];
        return (e.ipfsHash, e.timestamp, e.submittedBy);
    }
}

